(window.webpackJsonp=window.webpackJsonp||[]).push([[1696],{215:function(r,n,v){"use strict";v.r(n);var b=v(2),_=Object(b.a)({},function(){var r=this,n=r.$createElement,v=r._self._c||n;return v("ContentSlotsDistributor",{attrs:{"slot-key":r.$parent.slotKey}},[v("title",[r._v("ST入门指北")]),r._v(" "),v("meta",{attrs:{name:"GENERATOR",content:"WinCHM"}}),r._v(" "),v("meta",{attrs:{"http-equiv":"Content-Type",content:"text/html; charset=gb2312"}}),r._v(" "),v("br"),r._v("ST入门指北 by冯德\n"),v("br"),r._v(" "),v("br"),r._v("本文是基于FX的规则和资源环境，简单叙述快速车怪法、怪物或挑战的强度判断，以及奖励发放的建议，希望对新进ST有所帮助。请结合D君的怪物手册食用，味道更佳。\n"),v("br"),r._v(" "),v("br"),r._v("※如何快速车怪：\n"),v("br"),r._v("首先，确定你要车什么等级的NPC，以此判断其属性和技能数据。\n"),v("br"),r._v("NPC总是有2个主属性，3个副属性，其他4项皆为通常属性；有2个主技能/专业，4个副技能/专业，其他技能皆为0。之后，再给予其同级双特性，这个NPC就车完了！\n"),v("br"),r._v("无支线：主属性为4，副属性为2，通常属性为1；主技能/专业为3，副技能/专业为1\n"),v("br"),r._v("D级：主属性为6，副属性为4，通常属性为2；主技能/专业为5，副技能/专业为3\n"),v("br"),r._v("C级：主属性为11，副属性为6，通常属性为3；主技能/专业为8，副技能/专业为4 \n"),v("br"),r._v("B级：主属性为16，副属性为11，通常属性为4；主技能/专业为11，副技能/专业为5 \n"),v("br"),r._v("A级：主属性为21，副属性为16，通常属性为5；主技能/专业为14，副技能/专业为8 \n"),v("br"),r._v("AA级：主属性为26，副属性为21，通常属性为6；主技能/专业为17，副技能/专业为11\n"),v("br"),r._v("S级：主属性为36，副属性为31，通常属性为16；主技能/专业为20，副技能/专业为14\n"),v("br"),r._v("一般来说，将主属性+主技能/专业分配给NPC的攻击——或者你希望它最突出的特色，例如善于破解防火墙的黑客、能歌善舞的明星，诸如此类。\n"),v("br"),r._v("其基础防御值、速度、豁免等，依照你分配的属性决定。\n"),v("br"),r._v("在分配完毕这些数据后，给予其同级双特性，例如一个D级的NPC可能会获得一个2L的天生武器和2点天生防御，C级的NPC会获得20点移动速度和攻击上的4DP加值等。尽量不要把这些加值摞在一起——你可能会制造出一个可以秒杀玩家同时会被玩家秒杀，或者在输出上毫无建树也不会死的怪物——这对玩家来说无疑不是一个太好的体验。\n"),v("br"),r._v("用这种方法车出来的怪，如果其AI较低，亦不集群行动，即可认为是一个对同级玩家而言恶意值0的怪物（毫无挑战）。\n"),v("br"),r._v("之后，每给该怪物提升1个同级特性，其恶意值增加100；每提升一个支线等级（这意味着其属性、技能以及赠送的同级特性都会同步提升），其恶意值增加1500。3个同级特性可以换成一个高级特性。\n"),v("br"),r._v(" "),v("br"),r._v("※有关同级玩家：\n"),v("br"),r._v("我们定义玩家强化的最高等级的内部强化（血统、称号、技艺等会提供属性上限的强化）为其等级，即一个C级玩家一般肯定会有一个属性达到11；而实际上该玩家总共获得的支线应该已经达到了一个B（3个C）。\n"),v("br"),r._v("在此基础上，玩家每多强化一个同级支线（包括但不限于血统、改造、技艺、物品），傲天值上升200。每有1500傲天值，该玩家视为高一级支线的玩家进行计算。三个低级支线可以换算成一个高级支线。\n"),v("br"),r._v("将队伍中的每个玩家等级加和，除以队伍人数，即为队伍的平均等级。\n"),v("br"),r._v(" "),v("br"),r._v("※有关强度判断：\n"),v("br"),r._v("我们将强度分为以下等级：毫无难度、有挑战性、势均力敌、艰苦绝伦、九死一生、载入史册。分别对应着无奖励（或没有支线奖励，只有少量分数奖励）、D级奖励、C级奖励、B级奖励、A级奖励、S级奖励。\n"),v("br"),r._v("每当恶意值增加1000，难度评级即可上升一级。也即是说，对于上面的例子，一个D级人物（实际上约为3D级强化），当面对一个1000恶意值（D级属性、12个D级特性）的NPC并只能正面击败之的挑战称为“有挑战性”的挑战，可以获得一份D级奖励。\n"),v("br"),r._v("当然，这种情况较为少见。相对多见的情况是多个PC围攻1个BOSS——对于NPC的数量比玩家少的情况，NPC的数量每比玩家单位少一个，这次挑战的恶意点数下降10乘以2的（单位）次方；NPC数量比玩家多时，则上升这个数字。\n"),v("br"),r._v("其他上升或下降恶意值的情况见D君的怪物手册。\n"),v("br"),r._v(" "),v("br"),r._v("※范例流程：\n"),v("br"),r._v("1、计算队伍玩家的平均等级。\n"),v("br"),r._v("2、设计要面对的挑战等级。\n"),v("br"),r._v("3、统计你的恶意值，并根据恶意值放怪。\n"),v("br"),r._v("例如，主持人条子发放了4D支线给玩家，因此每个玩家可以视为一个D级300傲天值的玩家。队伍平均等级为D。\n"),v("br"),r._v("条子希望在这一场战斗后，队伍整体升级到C级，因此他需要一个“势均力敌”的挑战。这会让他的怪增加2000恶意值；然后将剩余的300傲天值也换算成恶意折合进来，这样的话，每只怪的恶意值是2300。\n"),v("br"),r._v("他首先花费1500恶意值将怪的基础支线等级提升到C；紧接着设计了集体作战（数量与玩家相同，有一定配合性，消耗100恶意值）、智力较低（优先攻击最近和输出伤害最高者，不会被放风筝，消耗100恶意值），最后剩下600恶意值，他决定换成1个C级特性和3个D级特性强化怪物本身。\n"),v("br"),r._v("让我们祈祷他的PC能好运吧。\n"),v("br"),r._v(" "),v("br"),r._v("※一场综合性挑战：\n"),v("br"),r._v("战斗是无限的主旋律，但无限并不仅仅是战斗。智计百出、因势利导，长袖善舞、借势压人，观察入微、有备而战……都是无限的一部分。\n"),v("br"),r._v("一场完整的挑战，可能包含了以上的部分或全部。\n"),v("br"),r._v("因为PC喜欢搞事情，所以很难说这些挑战的难度等级都是多少。\n"),v("br"),r._v("因此在这里仅提供一个“该等级进行的恶意值0”的DC。\n"),v("br"),r._v("无支线：2\n"),v("br"),r._v("D：4\n"),v("br"),r._v("C：7\n"),v("br"),r._v("B：11\n"),v("br"),r._v("A：16\n"),v("br"),r._v("S：20\n"),v("br"),r._v("例如，一队D级玩家——其中应当不是全部弱智——要破解一个写在门上的谜题，进行智力+学识的检定，该检定的DC为4可以视为恶意值0。之后，视能否重试、是否为任务中不可绕过的坎、是否有其他线索能降低DC等因素，增加或减少恶意值（心判）。\n"),v("br"),r._v("总之，如果看PC通关的比较辛苦，就多发点奖励吧（合十）……什么你不愿意？！你丫的恶意值已经突破天际啦！\n"),v("br"),r._v(" "),v("br"),r._v("※奖励的发放标准：\n"),v("br"),r._v("一般来说，我们建议支线与分数的比例为D对应1500点，每上升1级则点数翻倍，即D+1500，C+3000，B+6000，以此类推（当然，还有每部影片的保底1000点不计算在内）。在这个比例以上的，可以认为是ST的善意；在这个比例以下，则可能出现玩家没有足够的分数去补强属性或技能的情况。\n"),v("br"),r._v("建议在剧情中获得的奖励，给玩家尽可能的平均分配。不建议放置“只有击杀者能获得奖励”的怪物，除非你同时设定“高等级玩家击杀无奖励，低等级玩家击杀获得奖励”——希望用这种方式来在剧情中补齐高、低级玩家的支线差。\n"),v("br"),r._v("PC也是人，比较带来不满。愚以为，TRPG还是一个社交游戏，作为ST，应该带给玩家的是“很棒的游戏体验”，而非“绝对公正的判决”。\n"),v("br"),r._v(" "),v("br"),r._v("※XP的发放：\n"),v("br"),r._v("XP不一定是额外奖励，XP不一定是额外奖励，XP不一定是额外奖励。\n"),v("br"),r._v("重要的事情说三遍。\n"),v("br"),r._v("XP的主要用处，是调剂ST发放奖励的种类。例如当你想给PC发放DD支线时，可以将其中的一个D转化为9XP发放出去——支线与XP的换算见规则中的自创技能部分——这样可以给希望提升自己专长，或者希望强化只能以XP购买的强化的玩家使用。\n"),v("br"),r._v("你可以自己诠释这部分XP，比如基础XP、解谜XP、出勤XP，等等。拆XP的部分可以稍微少一点。一场不要超过20点（大约补一点就够买一个C级强化）的程度为好。\n"),v("br"),r._v("在此基础上，可以给予那些表现较好、出勤很齐、解开关键谜题……等等的玩家额外XP。推荐1~3XP即可。尽量不要让玩家之间的奖励差距过大，这会导致一个平均挑战等级的怪物对一部分玩家毫无挑战，对另一部分玩家极具挑战性——当然，你已经决定下场车死某个奖励太多或太少的玩家的话，另说。\n"),v("br"),r._v("最后，千万不要照着触发角色特性或者美德恶德给XP，奖励扮演不是这么奖励的。这只会让玩家将自己的特色纷纷定位在诸如爱护队友、狂躁易怒等容易触发的特性上。\n"),v("br"),r._v("顺便吐槽一句。\n"),v("br"),r._v("愚以为，舍己救人，贪婪成性等等——足以对人物产生影响，甚至危及到生命的表现，才可以被称为“闪光点”和“角色特性”。例如“守护者”，应该是当你们如同羊群面对猛虎，而你挡在所有人前面准备冲锋，才可称之为闪光——\n"),v("br"),r._v("whatever，要是有一个帮队友盖被子也能回复意志的团，请叫上我。\n"),v("br"),r._v(" "),v("br"),r._v("※强度的控制：\n"),v("br"),r._v("因为本规则对于属性上限的限制（吹），强弱差距不大（吹），滥强的土壤较少（吹），所以不用担心强度问题（啥？！）\n"),v("br"),r._v("好吧。\n"),v("br"),r._v("在这里提供一个简单的办法，以影响尽量少的方式控制玩家强度差距：\n"),v("br"),r._v("“选开规则：支线无法向上合成。”\n"),v("br"),r._v("很好，你现在准备了一个给B级玩家的模组，那么你可以发放给PC3个B级支线，以此保证你的PC们都是B级人物，不多不少。\n"),v("br"),r._v("什么？你担心XP可以攒？玩家之间可以交易？\n"),v("br"),r._v("……啊哈哈哈，这个的确没有办法。请自行跟你的PC们协调吧~\n"),v("br")])},[],!1,null,null,null);n.default=_.exports}}]);