(window.webpackJsonp=window.webpackJsonp||[]).push([[584],{1095:function(r,n,v){"use strict";v.r(n);var b=v(2),_=Object(b.a)({},function(){var r=this,n=r.$createElement,v=r._self._c||n;return v("ContentSlotsDistributor",{attrs:{"slot-key":r.$parent.slotKey}},[v("title",[r._v("克苏鲁形意拳")]),r._v(" "),v("meta",{attrs:{name:"GENERATOR",content:"WinCHM"}}),r._v(" "),v("meta",{attrs:{"http-equiv":"Content-Type",content:"text/html; charset=gb2312"}}),r._v(" "),v("br"),r._v(" "),v("br"),r._v("克苏鲁形意拳\n"),v("br"),r._v("介绍：\n"),v("br"),r._v("    原名《阿萨托斯核心观想法》，不过其总纲和大部分内容都已经残缺。\n"),v("br"),r._v("    剩下的招数大多是外来之神的大祭司-克苏鲁传下的功夫。\n"),v("br"),r._v("    练习这门拳法的第一步便是“死”。\n"),v("br"),r._v("    唯有让作为凡人的你“死去”，方可像伟大的克苏鲁般融入万古不变的诡异，在死亡亦会消逝的妙境中涅槃。\n"),v("br"),r._v(" "),v("br"),r._v("D级\n"),v("br"),r._v("不可名状架势：D+750\n"),v("br"),r._v("    使用此架势，你需观想位于海底的拉莱耶城，以此强化你与异形诸神的联系。\n"),v("br"),r._v("    以一个标准动作做出一次攻击，此次攻击附带2点威猛点数和一点不可豁免的恐惧点数。\n"),v("br"),r._v("    特别的，若使用一点意志值，你可对射程十米以内的某个敌人进行精神上的攻击。其伤害类型转为精神伤害，且其威猛值+2。\n"),v("br"),r._v("    不过特殊的，你无法将自身天武或持有的武器伤害，运用在这种特殊用法上。\n"),v("br"),r._v(" "),v("br"),r._v("延命长生法：D+500\n"),v("br"),r._v("    修行此拳法，可将自身窍穴和虚空中的异形之神感应。\n"),v("br"),r._v("    将其部分神意摄入体内，以此获得长生。\n"),v("br"),r._v("    以移动动作进行一次移动，同时启动此能力；本轮内你免疫B级衰老效果、获得额外2/魔法，且恢复十年自然寿命。\n"),v("br"),r._v("    作为代价，每次续命你都需要通过一次DC为5的强韧判定，若失败则你的肉身的一部分会发生畸变。\n"),v("br"),r._v("    例如眼珠变得凸出、屁股上长出眼珠，这会使你的相貌逐步脱离人类的喜好，生长出部分异形的特征。\n"),v("br"),r._v(" "),v("br"),r._v(" "),v("br"),r._v("C级\n"),v("br"),r._v("达贡／海德拉的重击：C+1000\n"),v("br"),r._v("    运用此招式，必须观想父神达贡与母神海德拉雄壮的身姿，以此调动全身肌肉的力量。\n"),v("br"),r._v("    以一个标准动作做出一次攻击，在这一击中你视为比原本的体积值提高10点，即获得触及额外+4米、力量判定+5、伤害上限+5，且视为额外具备【晕眩】特性。你可以同时在这一击中额外进行一次冲撞。\n"),v("br"),r._v(" "),v("br"),r._v(" "),v("br"),r._v("B级\n"),v("br"),r._v("克苏鲁之巨力擒缚：B+2000\n"),v("br"),r._v("    据说有类似于如同名法术效果，不过有如下不同。\n"),v("br"),r._v("    首先你需要进行观想如下画面。\n"),v("br"),r._v("    当天上的群星正位之时，沉没在大洋深处的拉莱耶城浮出水面。\n"),v("br"),r._v("    你化身为一尊面如章鱼、背负双翼的巨人，以四海倾斜的巨力朝着敌人轰去。\n"),v("br"),r._v("    使用一个整轮动作启动，且进行一次攻击。你可用此拳法同时擒拿多个目标，可包含所有自身触及范围内的存在。\n"),v("br"),r._v("    被命中的敌人需要通过一次意志判定进行豁免，其DC为16点，若失败则将直接进入定身状态，持续1轮。\n"),v("br"),r._v("　　这种拳术的攻击会对敌人的力量属性造成属性伤害，其数值和上述DC相同、同样可进行取高，以意志豁免抵消。这样造成的力量属性伤害不可使对方属性降至0。\n"),v("br"),r._v("　　若一直以整轮动作维持专注，则可在抽取力量的同时，将定身状态维持下去。\n"),v("br"),r._v(" "),v("br"),r._v(" "),v("br"),r._v("BB级\n"),v("br"),r._v("克图格亚之核融爆破拳：BB+3500\n"),v("br"),r._v("　　使用此拳法需要一个标准动作，且同时消耗6点意志值。\n"),v("br"),r._v("　　你必须在攻击的同时观想如下场景。\n"),v("br"),r._v("　　一尊太阳降临于熔岩的大地上，率领着祂的孩子们对世间的混沌发着毁灭性的战争。\n"),v("br"),r._v("    此次攻击中，你可忽略共计12点防御值，需要在攻击前自行选定类型。\n"),v("br"),r._v("　  你仅需进行接触，敌人的肉体内部便开始进行核反应，且因此受到伤害。\n"),v("br"),r._v("　　你这次攻击视为接触攻击，且视为具有【能量武器】特性。\n"),v("br"),r._v("　　你造成的伤害类型分为多层模式。\n"),v("br"),r._v("　　首先为物理伤害，这是你的拳法本身所造成的伤害；不过你也可将其彻底转换为【暗劲】，伤害类型改为音波。\n"),v("br"),r._v("　　其次为核反应衍生伤害，分为三层——光辐射、贯穿核辐射以及连锁式裂变反应。光辐射造成光能伤害、贯穿核辐射点数视为同时具备恶心和疲乏异常效果的异常点数。连锁式裂变反应，则造成恶意的燃烧异常点数，敌人需按照燃烧解除方式进行解除。以上核反应衍生伤害的数值等于你拳法本身造成伤害数，以强韧豁免抵消。\n"),v("br"),r._v("　　偶有传闻，这种拳法并非源于爆燃者的传授，祂的死敌-奈亚拉托提普方为传授“核”之力量的祖师。\n"),v("br")])},[],!1,null,null,null);n.default=_.exports}}]);