(window.webpackJsonp=window.webpackJsonp||[]).push([[588],{1104:function(r,n,v){"use strict";v.r(n);var b=v(2),_=Object(b.a)({},function(){var r=this,n=r.$createElement,v=r._self._c||n;return v("ContentSlotsDistributor",{attrs:{"slot-key":r.$parent.slotKey}},[v("title",[r._v("六脉神剑")]),r._v(" "),v("meta",{attrs:{name:"GENERATOR",content:"WinCHM"}}),r._v(" "),v("meta",{attrs:{"http-equiv":"Content-Type",content:"text/html; charset=gb2312"}}),r._v(" "),v("br"),r._v("六脉神剑经(武学招式) \n"),v("br"),r._v("BB+3000\n"),v("br"),r._v("先决:B级内力、智力5+、敏捷5+、耐力5+、生存（医治）5+、武技(指)5+、(剑)5+\n"),v("br"),r._v("六脉神剑，并非真剑，乃以浑厚内力的指力发出六种内力，含於指尖的内力隔空激发出去，使其以极高速在空中运动的一门技术，做架简单，功效卓著，感应强烈，均为首屈一指，久习可得奇效。达到指剑的境界，即指力所能及的地方，有如有一柄无形的剑，无论是横扫或虚指，均可伤敌，剑气有质无形，出剑时急如电闪，迅猛绝伦，交叉运用，以气走剑杀人於无形，异常神奇，堪称剑中无敌，可称无形气剑。\n"),v("br"),r._v(" "),v("br"),r._v("以一个自由动作并花费1点内力可以在指上形成六脉剑气，持续1轮，人物可以将剑气当作体积3的普通长剑使用，它拥有[破甲3]，武技上每1点附加成功都能够忽略1点指定部位减值，能够使用剑招。\n"),v("br"),r._v("每学习一个剑招都会使六脉剑气+1L，每学会3个，加骰降低1级，最低到8。当你学习完毕所有六脉神剑剑招后，剑气视为拥有[神兵]特性。\n"),v("br"),r._v(" "),v("br"),r._v("以下技能可以单独购买，使用条件如上，但不会获得增幅效果和六脉神剑经带来的好处。\n"),v("br"),r._v("六脉神剑的招式都具有[能量武器]特性，拥有8点高速优势；可以正常攻击灵体。在每次攻击中，只能用对应的手指，附带单一的六脉神剑招式。\n"),v("br"),r._v("如无说明，射程都是50米。\n"),v("br"),r._v(" "),v("br"),r._v("右手剑:\n"),v("br"),r._v("六脉神剑之少商剑\n"),v("br"),r._v("B+2000\n"),v("br"),r._v("右手拇指－少商剑〈特点：剑路雄劲，颇有石破天惊，风雨大至之势〉。\n"),v("br"),r._v("以六脉神剑作出一次武技(剑)或(指)攻击检定，由於少商剑的内力属性雄浑有力，因此只能用力量作为关键属性。\n"),v("br"),r._v("少商剑不会使这次攻击带来实质的加值，但会额外获得6点破甲和破魔。\n"),v("br"),r._v("使用本剑招时，武技上每有1点附加成功可以忽略1点DR。\n"),v("br"),r._v("增幅:每额外支付1点内力，可以使剑气伤害+1L，上限是内力等级。\n"),v("br"),r._v(" "),v("br"),r._v("六脉神剑之中冲剑\n"),v("br"),r._v("B+2000\n"),v("br"),r._v("右手中指－中冲剑〈特点：大开大阖，气势雄迈〉。\n"),v("br"),r._v("以六脉神剑作出一次武技(剑)或(指)攻击检定，由於中冲剑的内力属性雄浑有力，所以只能用力量作为关键属性。\n"),v("br"),r._v("中冲剑是挥砍攻击，拥有[威猛3][破甲3]特性，对方体积每比5大2点追加1点基础防御减值，相反，对方体积每比5小1点这次攻击减少2点自动成功。\n"),v("br"),r._v("可以选择对射程范围内进行造成伤害等于[3+六脉剑气伤害]的『火力压制』攻击。\n"),v("br"),r._v("使用本剑招时，武技上每有1点附加成功，伤害+1L。\n"),v("br"),r._v("增幅:每额外支付1点内力，可以使剑气伤害+1L，上限是内力等级。\n"),v("br"),r._v(" "),v("br"),r._v("六脉神剑之少冲剑\n"),v("br"),r._v("B+2000\n"),v("br"),r._v("右手小指—少冲剑〈特点：轻灵迅速〉。\n"),v("br"),r._v("以六脉神剑作出一次武技(剑)或(指)攻击检定，由於少冲剑的内力属性轻灵迅速，因此只能用敏捷作为关键属性。\n"),v("br"),r._v("少冲剑是穿刺攻击，并额外增加6点高速。\n"),v("br"),r._v("使用本剑招时，武技上每有1点附加成功，高速再+1。\n"),v("br"),r._v("增幅:额外花费3点内力，可以用移动动作施展附带本剑招的攻击。\n"),v("br"),r._v(" "),v("br"),r._v(" "),v("br"),r._v("左手剑:\n"),v("br"),r._v("六脉神剑之商阳剑\n"),v("br"),r._v("B+2000\n"),v("br"),r._v("左手食指—商阳剑〈特点：巧妙灵活，难以捉摸〉。\n"),v("br"),r._v("以六脉神剑在虚招检定中使用。由於商场剑的内力属性灵活巧妙，因此可以用敏捷取代智力作为关键属性。\n"),v("br"),r._v("人物在虚招检定中可以对对方造成胜出数的伤害。\n"),v("br"),r._v("使用本剑招时，武技上每有1点附加成功，在虚招检定中得到1DP加值。\n"),v("br"),r._v("增幅:每往上支付1点内力使伤害+1L，上限是内力等级。\n"),v("br"),r._v(" "),v("br"),r._v(" "),v("br"),r._v("六脉神剑之关冲剑\n"),v("br"),r._v("B+2000\n"),v("br"),r._v("左手无名指—关冲剑〈特点：以拙滞古朴取胜〉。\n"),v("br"),r._v("以六脉神剑作出一次武技(剑)或(指)攻击，或格挡动作中使用。在这次攻击时得到+6DP技艺加值或或格挡时获得6点额外格挡防御。由於关冲剑的内力属性关系，在攻击中可以自由选择力量和敏捷其中一个作为主要属性。\n"),v("br"),r._v("每轮需要用1点内力维持，这是一个自由动作。\n"),v("br"),r._v("使用本剑招时，在以武技(剑)或(指)格挡时，可以将另一个专业等级的一半作为额外的格挡防御。\n"),v("br"),r._v(" "),v("br"),r._v(" "),v("br"),r._v("六脉神剑之少泽剑\n"),v("br"),r._v("B+2000\n"),v("br"),r._v("左手小指—少泽剑〈特点：忽来忽去，变化精微〉。\n"),v("br"),r._v("以六脉神剑花费5点内力使用，一次武技(剑)/(指)攻击，在之前的回合每使用一次剑招(包括少泽剑)都会使消耗的内力-1。由於少泽剑的内力属性变化精微，因此可以用智力作为检定时的主要属性。\n"),v("br"),r._v("使用少泽剑时，获得8加骰；若已经是8则+3附加成功，技艺加值。\n"),v("br"),r._v("在这次攻击中，对方视为B级措手不及。\n"),v("br"),r._v("武技上有1点附加成功视为已支付1点内力进行以下两个增幅。\n"),v("br"),r._v("使用本剑招时，武技上每有1点附加成功，可以令这次攻击成为短点射/长点射/连射。\n"),v("br")])},[],!1,null,null,null);n.default=_.exports}}]);