(window.webpackJsonp=window.webpackJsonp||[]).push([[735],{1325:function(r,n,v){"use strict";v.r(n);var b=v(2),_=Object(b.a)({},function(){var r=this,n=r.$createElement,v=r._self._c||n;return v("ContentSlotsDistributor",{attrs:{"slot-key":r.$parent.slotKey}},[v("title",[r._v("无双道")]),r._v(" "),v("meta",{attrs:{name:"GENERATOR",content:"WinCHM"}}),r._v(" "),v("meta",{attrs:{"http-equiv":"Content-Type",content:"text/html; charset=gb2312"}}),r._v(" "),v("br"),r._v("▓无双道 \n"),v("br"),r._v(" "),v("br"),r._v("▓ 异世界的武将在奇伟的幻城中齐聚一堂，而残酷的争斗或厮杀早已开始；一人主宰战场的奇迹时代，如此的威力被英魂记载。 \n"),v("br"),r._v(" "),v("br"),r._v("▓武学分类 \n"),v("br"),r._v("「长柄」/「步法」/「心法」 \n"),v("br"),r._v(" "),v("br"),r._v("▓在购买了无双道之后，你打开的能量池为「无双」（耐力+感知），无双是内力的的变种，当你已经有内力时，无双将优先覆盖内力，并总值视为在原本内力总值的基础上重复开启内力后的总值，之后再次开启内力这个能量池时，也都视为重复开启无双。无双可以用于推动所有内力推动的招式，但是不包括“在特定条件下，内力也可以推动”的招式，只有无双才能够推动无双道中记录的招式。无双除了内力的补充方法外，每一次击杀一个敌人都将回复一点无双。 \n"),v("br"),r._v(" "),v("br"),r._v("▓魂之武装 \n"),v("br"),r._v(" "),v("br"),r._v("无双道是英魂的执念的具象化，并不具备真正意义上的「武经」，你需要选择一个确实存在的武器或者直接导致一个固定武器武器产生的物品（如兑换了斗流血法—火神之后的扎普牌打火机，或者灵能聚幅装置），那个物品成为无双道武经的载体，这会让它（或它产生的那个武器）的外貌更加威猛或者精致。 \n"),v("br"),r._v(" "),v("br"),r._v("魂之武装之间如果没有特殊说明，具现高级魂之武装也同时将具现低级魂之武装。具现魂之武装期间，无双不会因为自然休息而恢复。 \n"),v("br"),r._v(" "),v("br"),r._v("B级——「乱」: 在具现魂之武装期间，持有者的身心受到英魂的影响，持有者将无可避免的变得洒脱或古板，信守忠臣的理念等。在这期间，以无双道做出的攻击将会受到与（持有者意志总值的一半或武技技能）取低等值的dp数值加强。 \n"),v("br"),r._v("A级——「心」: 在这个等级，人物的身上明显的散发出红色的雾气，这是引导了英魂之力的佐证。这个阶段，人物使用无双道造成的伤害在结算时可以追加一次能量（灼伤／冰冻／电击）伤害，现阶段这次能量伤害为5L，并且与攻击结算一起结算。 \n"),v("br"),r._v("S级——「霸」: 在这个等级，A级时在攻击结算时追加的能量伤害增加为10A，且这些能量伤害被认为是“斩”属性的能量攻击，只能被全能量吸收所抵消（全伤害吸收也不可抵消）。 \n"),v("br"),r._v(" "),v("br"),r._v("▓S级武魂合击技： \n"),v("br"),r._v("魂体心—天下唯我 \n"),v("br"),r._v("在进入战斗时（不一定是决定先攻时），你可以宣告打开“魂体心—天下唯我”，在这个状态下，你的背后会出现体积为11，无法被攻击和影响的赤红色为将魂实影，你在这个状态下免疫任何对你产生心智，意志相关的效果（S级心灵免疫），包括士气加值与减值，你的行动将会只受你的控制（若你遭受某个可以控制你自主行动的效果，且该效果启用免疫等级，即标注X级XX来源效果，则你在对抗此效果时，视为S级支线等级的免疫），你可以以步行的方式飞行并穿越无特殊描述的物体，你免疫困难地形带来的不良影响，也不会受到因环境带来的对移动力的影响，同时，你每进行一个有战斗的回合，就将消耗两点无双，你每受到一点伤害，就将消耗一点无双，此外，你每回合都将消耗一点无双。 \n"),v("br"),r._v(" "),v("br"),r._v("▓记载武学 \n"),v("br"),r._v("D级：大蛮落「通用」 \n"),v("br"),r._v("用尽全力，放弃防御向下重劈的招式中的精髓，适用这个武学时，每回合消耗一点无双，你所有的攻击都必须是全力攻击，此时全力攻击并不会被视为一个单独的动作（而是在一次动作中附加的状态），如果是不能够适用全力攻击的攻击，则改为承受攻击dp+2，闪避防御-1（这个时候不视为全力一击）；且全力攻击对你闪避防御的减值改为一点；无论如何，这次攻击会获得额外的「高速1」「威猛1」和「破甲1」；即便是使用枪械或者弓箭一类的远程武器进行攻击，只要对手在你的近战范围内，你就能够使用这个武学。 \n"),v("br"),r._v(" "),v("br"),r._v("D级：关外杨柳怨关门「步法」 \n"),v("br"),r._v("这是一个步法，在你学会这个步法后，且无双不为空时，这个步法即可适用（并不意味着生效，此处，在你的无双归零时，这个步法即会失效，速度上的提升将暂时无法使用，并且将返还被扣除的三点无双，这个时候，在你的无双不大于三时，这个步法依旧不适用，但是可以使用主动效果）。你的无双上限扣除三个单位，速度永久性的增加20米，在战斗中，每一次受到攻击之前都可以宣言这个步法，以支出一点无双，在那次攻击结束前换取两点闪避防御。在你的武经等级提高后，你可以多支出（无双道等级）点无双，额外换取等量的闪避防御。 \n"),v("br"),r._v(" "),v("br"),r._v("C级：枯木又春「心法」 \n"),v("br"),r._v("适用这个心法后，你无双的自然回复速度翻倍（由二十四小时变为十二小时），你通过打坐恢复无双时，在判定时获得+1的附加成功，你每斩杀一个敌人都会改为获得2点无双，每回合一次，你只需要造成5L以上的伤害就可以恢复1点无双。 \n"),v("br"),r._v(" "),v("br"),r._v("C级：青龙啸「长柄」 \n"),v("br"),r._v("以半圆周极快速挥舞手中的武器，以这样的方式同时攻击周围的多名敌人，在异界武将中，正有人以这种武技作为自己的专擅之技，在他手中，刀划破空气的声音就如同青龙的长啸一般。标准动作使用，消耗两个单位的无双，以白刃攻击作为基础进行判定，这一次白刃攻击将额外拥有「高速3」，「威猛3」的特性。这次白刃攻击可以同时攻击多名在你的白刃攻击接触范围内的敌人，由你惯用手的那侧起算，每攻击一个敌人这次攻击造成的最终伤害就要依次减少1点。 \n"),v("br"),r._v("可选规则：如果你的人物力量超过16，且戴着绿帽子，并且使用长柄刀，则你可以选择额外支出八点无双，并宣告“青龙破”，这是一次特殊的青龙啸，不受到因为攻击多个目标而受到的伤害减值，并且无双凝成青色的气刃，这一次的白刃接触范围变为两倍，且这次攻击有至多一半的伤害被视为纯能量伤害。 \n"),v("br"),r._v(" "),v("br"),r._v("B级：狮子奋迅「步法」 \n"),v("br"),r._v("自由动作启动，每回合将消耗5点无双，你在攻击获得8点速度加值，你忽略受到多次攻击造成的防御减值，且你的一次攻击做五次计算那样减少对方的防御；在这个武学适用期间，你每回合多支出5点无双的话，则可以增加50米移动速度。 \n"),v("br"),r._v(" "),v("br"),r._v("A级：国士无双「心法」 \n"),v("br"),r._v("自由动作启动，你每斩杀一个敌人都会改为获得5点无双，你获得或额外增加（武经等级）点天生防御，你的每一次攻击都获得或额外增加「威猛5」，「破甲5」，你的基础移动速度将提升120米，你在做任何运动或驾驶的相关判定时，额外获得5点dp加值，此外，你将免除自然坠落伤害。 \n"),v("br"),r._v(" "),v("br"),r._v("S级：森罗万象「长柄」 \n"),v("br"),r._v("适用这个武学时，每回合将消耗10点无双，你的攻击判定增加32点dp无名加值，你的防御上增加32点洞察加值，在攻击防御高出你的攻击dp的敌人时，你将至少保有5点攻击dp，你的移动速度增加200米，你所有运动或驾驶的相关判定增加2点附加成功。 \n"),v("br")])},[],!1,null,null,null);n.default=_.exports}}]);