(window.webpackJsonp=window.webpackJsonp||[]).push([[9],{236:function(r,_,v){"use strict";v.r(_);var b=v(2),n=Object(b.a)({},function(){var r=this,_=r.$createElement,v=r._self._c||_;return v("ContentSlotsDistributor",{attrs:{"slot-key":r.$parent.slotKey}},[v("html",[v("head",[v("meta",{attrs:{"http-equiv":"Content-Type",content:"text/html; charset=gb2312"}}),r._v(" "),v("title",[r._v("属性概述")])]),r._v(" "),v("body",[v("div",{attrs:{align:"center"}},[v("span",{staticClass:"bbc_size",staticStyle:{"font-size":"18pt"}},[v("span",{staticClass:"bbc_color",staticStyle:{color:"red"}},[r._v("属性概述")])])]),r._v(" "),r._v("\n属性代表了人物自身具有的能力，这些能力基于天生，并且可以后天锻炼，或者用异能和武学进行强化。任何正常人的每一项属性值都至少为1。"),v("br"),r._v("\n人物的绝大多数判定都需要依据自身的属性，因此属性就相当于人物的能动能力和行为的可能性，并且有时也等同于你保持自身不被干扰或侵害的能力。若人物拥有某种超自然的本质或特性，属性也可能以超自然的形式发生作用。"),v("br"),r._v("\n属性值表示人物在这一方面的能力如何。一般而言，属性值2是现代人类的平均水准，而属性值5差不多是正常人类所能达到的极限。从属性值6开始，角色获得了超越凡人的力量，达到了传说中英雄们的水准，而属性超过15的人则踏入了半神的领域，超过25之后，在凡人眼中他们就是神祗一般的存在了。"),v("br"),r._v("\n当角色的属性（初始属性+购买+强化）增加到6点的时候，自动获得1点附加成功，之后每增加5点属性（11，16）就可以获得1点附加成功。关于属性上的附加成功的具体描述见下。"),v("br"),v("br"),r._v(" "),v("div",{attrs:{align:"center"}},[v("span",{staticClass:"bbc_size",staticStyle:{"font-size":"18pt"}},[v("span",{staticClass:"bbc_color",staticStyle:{color:"red"}},[r._v("属性的获得")])])]),r._v(" "),r._v("\n人物可以用奖励点兑换属性。"),v("br"),r._v("\n我们将属性分为以下两个部分，"),v("B",[r._v("基础属性")]),r._v("：它是你建卡时的属性，通过XP或分数购买属性改变的都是你的基础属性。以及"),v("B",[r._v("强化属性")]),r._v("：它是你通过强化获得的属性值（见下文）的总合，包括了内在加值、修行加值等；某些强化可能会减少你的属性，也记在这个部分中。需要注意的是，强化属性不一定是正数。"),v("br"),r._v("\n你最终的属性即为基础属性和强化属性之和。"),v("br"),r._v(" "),v("br"),r._v("\n基础属性在4以下时，提升1点基础属性的价格为200点；当基础属性在4及以上时，基础属性提升1点的价格为【已购买该属性的次数（购买第一次时记做1）*200分】，购买次数从基础属性4开始计算。因此当原本力量为1时，将力量从1提升到6需要先花费600点提升到4，紧接着购买2次，即200+200+200+200+400=1200分"),v("br"),r._v("\n人物也可以用经验值兑换属性，其价格为需要花费的每200点=1XP。"),v("br"),r._v("\n人物可以交替使用分数和XP购买属性，但购买该属性的次数是累加的。例如某玩家花费200分购买感知4→5，这是第一次购买；2XP购买5→6，这是第二次购买；600分购买6→7，这是第三次购买。"),v("br"),r._v(" "),v("br"),r._v("\n此外，人物可以通过强化来获取属性值。当人物强化血统、改造（包括功法）、瞳术、技艺、称号、能量时，会依照以下规则获得属性值的强化和分配：\n"),v("br"),r._v("玩家在无支线时，最多只能将某个属性达到5。这被称为"),v("font",{attrs:{color:"#FF0000"}},[r._v("【属性上限】")]),r._v("。超过属性上限的属性仍然存在，可以作为某些强化或装备要求的前提，但不会为你提供任何好处，包括检定、衍生属性、附加成功等，直到你将【属性上限】提升至超过你的属性值时，该属性才会生效。该限制同时对所有增加属性值的方式（包括内在加值、修行加值与临时加值）生效。简单来说，你最高等级的内在强化决定了你的【属性上限】，而你强化的每个内在强化都会给你提供自由属性点。\n"),v("br"),r._v("强化D级资源会给予玩家1点属性，属于内在加值，自由分配。你所有强化中最高等级为D时，【属性上限】为6。\n"),v("br"),r._v("强化DD级资源会给予玩家2点属性，属于内在加值，自由分配。你所有强化中最高等级为DD时，【属性上限】为8。\n"),v("br"),r._v("强化C级资源会给予玩家3点属性，属于内在加值，自由分配。你所有强化中最高等级为C时，【属性上限】为11。\n"),v("br"),r._v("强化CC级资源会给予玩家6点属性，属于内在加值，自由分配。你所有强化中最高等级为CC时，【属性上限】为13。\n"),v("br"),r._v("强化B级资源会给予玩家9点属性，属于内在加值，自由分配。你所有强化中最高等级为B时，【属性上限】为16。\n"),v("br"),r._v("强化BB级资源会给予玩家18点属性，属于内在加值，自由分配。你所有强化中最高等级为BB时，【属性上限】为18。\n"),v("br"),r._v("强化A级资源会给予玩家27点属性，属于内在加值，自由分配。你所有强化中最高等级为A时，【属性上限】为21。\n"),v("br"),r._v("强化AA级资源会给予玩家54点属性，属于内在加值，自由分配。你所有强化中最高等级为AA时，【属性上限】为26。\n"),v("br"),r._v("强化S级及以上资源，每个S会给予玩家全属性+9的奖励。你所有强化中最高等级为S时，【属性上限】为36；在这之后每有一个S，【属性上限】就会+15。\n"),v("br"),r._v(" "),v("br"),r._v("注1：赠送的能力并不会再次赠送属性点数，但购买的技能仍然会赠送。例如，玩家A购买了人造人血统，获得了能量射击技艺，这并不会给他提供额外的属性点数；但玩家A又单独购买了1个D级能量射击技巧，这次购买会给他带来1点自由属性。此限制对节省支线的特性，例如“购买XX强化时减少1个D支线”来说同样生效。简单来说，按照玩家实际花费的支线计算应该赠送的属性。\n"),v("br"),r._v("注2：用XP自创能力同样会获得属性，并仍然受到上文的支线等级-属性上限限制。尽管如此，ST仍然可以选择不开放自创能力规则，只通过发放支线-奖励点数来控制玩家的属性上限。此外，对于以某种方式节省XP的能力，如“你可以花费一半XP来自创一个白刃技艺”，则会完整地给予属性点；这也要求撰写者在撰写这类能力时需要计算可以额外获得的属性值，并使用特性DP去购买它。\n"),v("br"),r._v(" "),v("p",{staticStyle:{"font-size":"12pt"}},[v("I",[r._v("\n举例：玩家龙卷获得了1个D级支线剧情+1200奖励点。龙卷的智力为4，他希望自己可以尽可能高地提升智力，于是他首先购买了2次智力属性，因为这是他第1、2次购买，因此花费了200+400点；此时，他的智力属性为6，但因为他所有强化中最高等级为无支线，他的【属性上限】为5，因此他在进行智力检定时只能使用5点智力；在这之后，他强化了一个D级血统，其价格是D+600，并将血统赠送的属性点加在耐力上。此时，他的【属性上限】为6，他获得了智力上的1个附加成功。")]),r._v(" "),v("br")],1),v("div",{attrs:{align:"center"}},[v("span",{staticClass:"bbc_size",staticStyle:{"font-size":"18pt"}},[v("span",{staticClass:"bbc_color",staticStyle:{color:"red"}},[r._v("属性详细")])])]),r._v(" "),v("p",[v("br"),r._v("属性\n"),v("br"),r._v("属性代表了人物自身具有的能力，这些能力基于天生，并且可以后天锻炼，或者通过主神加以强化。任何正常人的每一项属性值都至少为1。\n"),v("br"),r._v("人物的绝大多数判定都需要依据自身的属性，因此属性就相当于人物的能动能力和行为的可能性，并且有时也等同于你保持自身不被干扰或侵害的能力。若人物拥有某种超自然的本质或特性，属性也可能以超自然的形式发生作用。\n"),v("br"),r._v("属性值表示人物在这一方面的能力如何。一般而言，属性值2是现代人类的平均水准。\n"),v("br"),r._v("属性达到5的人比较少见，在一般人中属于上乘。有特殊经历且极为突出的人可能达到6到10的水准，但这种人十分罕见，且一般不被人知晓。\n"),v("br"),r._v("属性值分为三大类：身体、精神、社交，分类如下：")]),r._v(" "),v("p",[v("br"),r._v("身体   精神   社交\n　"),v("br"),r._v("力量   　智力   　风度\n　"),v("br"),r._v("敏捷   　感知   　操控\n　"),v("br"),r._v("耐力   　决心   　沉着")]),r._v(" "),v("p",[v("br"),r._v("力量：\n"),v("br"),r._v("推拉物体、举起重物等的相关属性，包括了爆发力和负重能力，因此也影响奔走或挥动物体的速度、击打时所能造成的伤害程度。\n"),v("br"),r._v("任何能够对物体施加物理作用的存在都拥有力量值。\n"),v("br"),r._v("一点：勉强可以举起20公斤。\n"),v("br"),r._v("两点：平均程度，可以举起50公斤。\n"),v("br"),r._v("三点：可以举起125公斤。\n"),v("br"),r._v("四点：可以举起200公斤以内。\n"),v("br"),r._v("五点：举起325公斤，一拳可打碎头盖骨。")]),r._v(" "),v("p",[v("br"),r._v("敏捷：\n"),v("br"),r._v("全身的协调能力、动作的精确性和灵活性、反应神经等，快速有效运动身体的能力。\n"),v("br"),r._v("任何能够活动和移动的物体都拥有敏捷值。\n"),v("br"),r._v("一点：动作笨拙，在咖啡店做服务生时会把水洒在客人身上。\n"),v("br"),r._v("两点：平均，这是普通程度。\n"),v("br"),r._v("三点：良好，有不错的运动神经。\n"),v("br"),r._v("四点：要是你愿意的话，可以去表演特技。\n"),v("br"),r._v("五点：超越人体极限的神速灵巧。")]),r._v(" "),v("p",[v("br"),r._v("耐力：\n"),v("br"),r._v("身体的健康程度和抵抗力、新陈代谢的活跃度等，抵挡伤病的能力。\n"),v("br"),r._v("任何活着的东西都拥有耐力值。\n"),v("br"),r._v("一点：你可能会被一阵强风吹走，一不小心就会感冒。\n"),v("br"),r._v("两点：挨个一、两拳还算没问题。\n"),v("br"),r._v("三点：体格不坏而且几乎没生过病。\n"),v("br"),r._v("四点：参加全程马拉松可能会有优秀的成绩哦。\n"),v("br"),r._v("五点：施瓦辛格一样强而有力。")]),r._v(" "),v("p",[v("br"),r._v("智力\n"),v("br"),r._v("逻辑思维的能力、计算能力、分析能力和记忆力等，有时也代表了人物对超自然能量的分配和控制。请注意，虽然下面以IQ举例，但智力跟智商并没有直接关系，智力1的人也可以设置精妙的布局（即使他可能平时经常丢三落四）。\n"),v("br"),r._v("任何能够理解语言并且能进行分析和推理的存在都拥有智力值。\n"),v("br"),r._v("一点：不怎么聪明，阿甘（IQ80）。\n"),v("br"),r._v("两点：普通程度（IQ100）。\n"),v("br"),r._v("三点：比一般人聪明（IQ120）。\n"),v("br"),r._v("四点：不只聪明，而且才华横溢（IQ140）。\n"),v("br"),r._v("五点：可以心算定积分。（IQ160）。")]),r._v(" "),v("p",[v("br"),r._v("感知：\n"),v("br"),r._v("留意环境并查觉变化的能力，洞察力、直觉、快速分辨细节的能力。这一属性代表人物的注意力和对信息的分辨能力，因此近视眼不一定感知就低。在超自然的意义上，感知代表了人物能在多细微的程度上感应超自然能量。\n"),v("br"),r._v("任何能够感知外界的存在都拥有感知值。\n"),v("br"),r._v("一点：就算你认真观察，还是无法把握全盘状况。\n"),v("br"),r._v("两点：虽然可能忽略小细节，但是大致上没问题。\n"),v("br"),r._v("三点：可以掌握周围的各项小细节。\n"),v("br"),r._v("四点：你几乎很少遗漏什么，没什么可逃过你的视线。\n"),v("br"),r._v("五点：优异，轻轻一瞥就看透一切。")]),r._v(" "),v("p",[v("br"),r._v("决心\n"),v("br"),r._v("人物果敢的决断力和决定的坚定程度，也代表了人物顽固的程度。在超自然意义上，代表了人物的精神的强度。\n"),v("br"),r._v("任何能够有目的行动的存在都拥有决心值。\n"),v("br"),r._v("一点：在麦当劳被人说服买了新推出的套餐。\n"),v("br"),r._v("两点：让纠缠你两个小时的乞丐大失所望。\n"),v("br"),r._v("三点：可以轻松打发上门的推销员。\n"),v("br"),r._v("四点：革命先烈……\n"),v("br"),r._v("五点：你的话就是誓言，你的行动就是准则。")]),r._v(" "),v("p",[v("br"),r._v("风度\n"),v("br"),r._v("人物的气质、风度和存在感，不由地引起旁人兴趣的能力。在超自然意义上，也代表了人物在多大程度上超脱了自然本质。虽然与相貌有关，但数值高并不意味着长得帅或美丽，数值低也不一定是丑八怪。\n"),v("br"),r._v("任何能够将自己与环境区分开的存在都拥有风度值。\n"),v("br"),r._v("一点：认识的人在路上碰到你，都会假装没看到，不和你打招呼。\n"),v("br"),r._v("两点：普通，还有几位朋友。\n"),v("br"),r._v("三点：几乎大部份的人都会不自觉的信任你。\n"),v("br"),r._v("四点：像磁铁一样吸引别人接近。\n"),v("br"),r._v("五点：有人明知被你卖了，也会帮你数钱。")]),r._v(" "),v("p",[v("br"),r._v("操控\n"),v("br"),r._v("影响他人的能力，言行的感染力，煽动别人、让别人依你的想法行事的能力。在超自然的意义上，这也代表了人物支配某些超自然或灵性的存在的能力。\n"),v("br"),r._v("任何能够表达自己意愿的存在都拥有操控值。任何操控值达到2或更高的个体，都应该具有某种明确、主动、自觉表达自己意图的手段，如语言。一个眼神可能也算是操控。\n"),v("br"),r._v("一点：除非事实摆在面前，否则别人总会忽视你的意见。\n"),v("br"),r._v("两点：就像大部份的一样，有时候你可以蒙混一些人一时。\n"),v("br"),r._v("三点：能让只点一个汉堡的客人改点套餐。\n"),v("br"),r._v("四点：就算做了坏事，也能让你的粉丝相信你是正确的或迫不得已或可原谅的。\n"),v("br"),r._v("五点：身佩六国相印。")]),r._v(" "),v("p",[v("br"),r._v("沉着\n"),v("br"),r._v("镇定自若的能力，头脑的反应速度，在压力下或紧急情况下保持冷静的能力。在超自然意义上，代表了人物心智的活力。\n"),v("br"),r._v("任何有意愿应对外界刺激的存在都拥有沉着值。\n"),v("br"),r._v("一点：反应迟钝，讨论时跟不上别人的思路，天然呆。\n"),v("br"),r._v("两点：打麻将不会放枪。\n"),v("br"),r._v("三点：你很少受惊吓或是呆住说不出话来。\n"),v("br"),r._v("四点：能够超前别人的想法，「哦，我早就说过了….」。\n"),v("br"),r._v("五点：在别人反应过来之前已经导出结论了。")]),r._v(" "),v("br"),r._v(" "),v("br"),r._v("\n属性值为0和空属性"),v("br"),r._v("\n属性值降低代表人物在某些方面受到了损伤，可能是身体，也可能是精神或灵魂。若属性值降为0，则人物会失去自主行动力——在游戏进行中，玩家失去了对人物的控制权。"),v("br"),r._v("\n力量为0的人物不能移动，无法承受自身的重量也无法移动肢体。他将会倒在地上。"),v("br"),r._v("\n敏捷为0的人物不能活动，关节僵死。若他的姿势恰当，他会僵硬无助地站着或蹲着，否则他会倒地。"),v("br"),r._v("\n耐力为0的人物会死亡。"),v("br"),r._v("\n智力为0的人物失去自主行动能力，会依原始本能行动，不记得任何东西也无法使用任何技能（除了移动身体和逃避危险）。"),v("br"),r._v("\n感知为0的人物失去对现实的感应，陷入无法醒来的迷梦。"),v("br"),r._v("\n决心为0的人物失去行为能力，只会漫无目地的呆坐或徘徊，无法使用任何技能并失去对现实的反应（包括对危险的反应）。"),v("br"),r._v("\n风度为0的人物失去意识。"),v("br"),r._v("\n操控为0的人物会完全精神失常并疯狂，无法进行任何可理解或有意义的行为。"),v("br"),r._v("\n沉着为0的人物失去行为能力和自制力，完全精神失常，不做任何行动且失去对现实的反应。"),v("br"),r._v(" "),v("br"),r._v("\n有些存在天生没有某些属性，这称为空属性。空属性的存在无法进行相关判定，也不会受到属性降低的效果。"),v("br"),r._v("\n以下是空属性的一些范例："),v("br"),r._v("\n普通的物品没有任何属性。机械装置拥有力量值，其敏捷值取决于操纵者的敏捷或技能。预编程和可以自行工作的电脑等设备拥有智力值。"),v("br"),r._v("\n动物没有智力值，它们没有理解语言和进行逻辑思维的能力。"),v("br"),r._v("\n普通的植物只有耐力值。它们同时拥有硬度值，但生命值依耐力值而决定。某些超自然的植物可能拥有其他属性。"),v("br"),r._v(" "),v("br"),r._v(" "),v("div",{attrs:{align:"center"}},[v("span",{staticClass:"bbc_size",staticStyle:{"font-size":"18pt"}},[v("span",{staticClass:"bbc_color",staticStyle:{color:"red"}},[r._v("属性的附加成功")])])]),r._v(" "),v("br"),r._v("主神的分数奖励最基本的作用就是强化自身的属性。无限恐怖中的轮回小队成员以及给他们带来危险的可怕敌人，往往都拥有远远超过现实中人类极限的各种属性。高属性带来的特殊效果如下：\n任何属性达到6时，都会获得“该属性上的1点附加成功”。该属性每比6高5，便会再获得1点附加成功。例如，约翰的力量是6，因此他在力量上具有1点附加成功。若他后来将力量提升到11，则他在力量上具有2点附加成功。\n属性上的附加成功主要有以下作用：\n"),v("br"),r._v("-当你使用该属性进行检定时，可以因此获得附加成功。\n"),v("br"),r._v("-你的敏捷与感知中较高的一项所具有的附加成功，可以作为防御的附加成功。使用1点防御的附加成功，可以抵消攻击的1个成功。若攻击的成功数被抵消到0，则本次攻击失手，附加成功无效。\n"),v("br"),r._v("-力量上的每1点附加成功，可以使人物的跳跃检定结果+1，并在力量检定上获得+2DP额外的增强加值。\n"),v("br"),r._v("-敏捷上的每1点附加成功，可以使人物的基础移动速度+5米。\n"),v("br"),r._v("-耐力上的每1点附加成功，可以使人物的生命值+2。\n"),v("br"),r._v("-智力上的每1点附加成功，可以使人物将一种语言提升1级，并获得1个专业点；并选择一个技能，将其提升1级，上限为3。\n"),v("br"),r._v("-感知上的每1点附加成功，可以使人物的基础感知距离增加20米。\n"),v("br"),r._v("-决心上的每1点附加成功，可以使人物的意志力池+2。\n"),v("br"),r._v("-风度上的每1点附加成功，可以使人物在进行机运骰和基因锁解锁检定（或类似剧情奖励检定）时在检定结果上+1。\n"),v("br"),r._v("-操控上的每1点附加成功，可以使人物在一天中重投一次任意检定（包括基因锁和机运骰）。你必须接受重投的结果，即使这次结果比上次更差。\n"),v("br"),r._v("-沉着上的每1点附加成功，可以使人物的先攻值+4。\n"),v("br"),r._v("有关附加成功的使用，详见"),v("STRONG",[v("SPAN",{staticClass:"bbc_color",staticStyle:{COLOR:"blue"}},[v("A",{attrs:{href:"../4.非战斗与战斗规则/4.1非战斗与战斗规则.htm"}},[r._v("非战斗与战斗规则")])],1)],1),r._v("小节。\n"),v("br"),r._v(" "),v("div",{attrs:{align:"center"}},[v("span",{staticClass:"bbc_size",staticStyle:{"font-size":"18pt"}},[v("span",{staticClass:"bbc_color",staticStyle:{color:"red"}},[r._v("属性的增加与减少")])])]),r._v(" "),v("strong",[v("span",{staticClass:"bbc_color",staticStyle:{color:"blue"}},[r._v("1.永久性的增加某项属性")])]),v("br"),r._v("\n通常而言，永久增加某项属性有三种办法："),v("br"),r._v("\na.购买具有支线等级的，强化自身的资源，包括血统、改造（包括功法）、瞳术、技艺、称号、能量，会获得每个D支线1点自由分配的属性。"),v("br"),r._v("\nb.用奖励点兑换属性，方式见上文。"),v("br"),r._v("\nc.用经验值兑换属性，方式见上文。"),v("br"),r._v("\n※b,c方式提供的加值都为可以叠加的内在加值。a方式如无说明默认为可以叠加的内在加值，此外，某些能力会提供属性上的修行加值。有关加值类型详见"),v("STRONG",[v("SPAN",{staticClass:"bbc_color",staticStyle:{COLOR:"blue"}},[v("A",{attrs:{href:"../5.特殊规则/5.3能力，模板，本质，加值类型.htm"}},[r._v("内在加值和修行加值")])],1)],1),r._v("。\n"),v("br"),r._v(" "),v("strong",[v("span",{staticClass:"bbc_color",staticStyle:{color:"blue"}},[r._v("2.永久性的降低某项属性")])]),v("br"),r._v("\n通常而言，永久降低某项属性只有一种情况："),v("br"),r._v("\n兑换某个永久会降低某项属性的能力。"),v("br"),r._v("\n兑换此类能力时，必须降低后的属性仍然高于1才能进行兑换，若兑换后属性会降低至0，兑换不能进行。"),v("br"),r._v("\n若属性降低导致你不再满足某些已有能力的前提条件，你仍然有这些能力，但它们暂时无法生效。"),v("br"),r._v(" "),v("br"),r._v(" "),v("strong",[v("span",{staticClass:"bbc_color",staticStyle:{color:"blue"}},[r._v("3.临时性的增加某项属性")])]),v("br"),r._v("\n临时增加属性的办法有许多种。"),v("br"),r._v("\n增加后的属性若使你满足某些能力的前提条件，你可以以当前的属性来兑换这些能力，当你不再满足这些前提条件时，你不会失去这些能力，但是它们暂时无法生效。此外，临时增加属性也无法令你突破属性上限。"),v("br"),r._v(" "),v("br"),r._v(" "),v("strong",[v("span",{staticClass:"bbc_color",staticStyle:{color:"blue"}},[r._v("4.临时性的降低某项属性")])]),v("br"),r._v("\n临时降低属性的办法有许多种。"),v("br"),r._v("\n降低属性的效果至多只能将人物的属性降低到0。"),v("br"),r._v("\n若属性降低导致你不再满足某些已有能力的前提条件，你仍然有这些能力，但它们暂时无法生效。"),v("br"),r._v("\n如果没有特殊说明，被临时降低的属性在每次长休息时恢复1点（长休息指8小时的睡眠等），这是一种自然恢复（因此，能提升自然恢复速率的能力能提升此恢复速度）。\n")],1)])])},[],!1,null,null,null);_.default=n.exports}}]);